class AppContentModel {
    
    let subDescription: String?
    let description: String?
    let image: UIImage?
    let thumbnail: UIImage?
    let title: String?
    let subtitle: String?
    let content: String?
    
    init(subDescription: String?, description: String?, image: UIImage, thumbnail: UIImage?, title: String?, subtitle: String?, content: String?) {
        self.subDescription = subDescription
        self.description = description
        self.image = image
        self.thumbnail = thumbnail
        self.title = title
        self.subtitle = subtitle
        self.content = content
    }
}

